import { SafeEventEmitter } from "@toruslabs/openlogin-jrpc";

import { BaseConfig, BaseState, IController } from "./interfaces";

/**
 * Controller class that provides configuration, state management, and subscriptions
 */
class BaseController<C extends BaseConfig, S extends BaseState> extends SafeEventEmitter implements IController<C, S> {
  /**
   * Default options used to configure this controller
   */
  defaultConfig: C = {} as C;

  /**
   * Default state set on this controller
   */
  defaultState: S = {} as S;

  /**
   * Determines if listeners are notified of state changes
   */
  disabled = false;

  /**
   * Name of this controller used during composition
   */
  name = "BaseController";

  private readonly initialConfig: C;

  private readonly initialState: S;

  private internalConfig: C = this.defaultConfig;

  private internalState: S = this.defaultState;

  /**
   * Creates a BaseController instance. Both initial state and initial
   * configuration options are merged with defaults upon initialization.
   *
   * @param config - Initial options used to configure this controller
   * @param state - Initial state to set on this controller
   */
  constructor({ config = {} as C, state = {} as S }: { config?: Partial<C>; state?: Partial<S> }) {
    super();
    // Use assign since generics can't be spread: https://git.io/vpRhY
    this.initialState = state as S;
    this.initialConfig = config as C;
  }

  /**
   * Retrieves current controller configuration options
   *
   * @returns - Current configuration
   */
  get config(): C {
    return this.internalConfig;
  }

  /**
   * Retrieves current controller state
   *
   * @returns - Current state
   */
  get state(): S {
    return this.internalState;
  }

  /**
   * Updates controller configuration
   *
   * @param config - New configuration options
   * @param overwrite - Overwrite config instead of merging
   * @param fullUpdate - Boolean that defines if the update is partial or not
   */
  configure(config: Partial<C>, overwrite = false, fullUpdate = true): void {
    if (fullUpdate) {
      this.internalConfig = overwrite ? (config as C) : Object.assign(this.internalConfig, config);

      for (const key in this.internalConfig) {
        if (typeof this.internalConfig[key] !== "undefined") {
          this[key as string] = this.internalConfig[key];
        }
      }
    } else {
      for (const key in config) {
        /* istanbul ignore else */
        if (typeof this.internalConfig[key] !== "undefined") {
          this.internalConfig[key] = config[key];
          this[key as string] = config[key];
        }
      }
    }
  }

  /**
   * Updates controller state
   *
   * @param state - New state
   * @param overwrite - Overwrite state instead of merging
   */
  update(state: Partial<S>, overwrite = false): void {
    this.internalState = overwrite ? { ...(state as S) } : { ...this.internalState, ...state };
    this.emit("store", this.internalState);
  }

  /**
   * Enables the controller. This sets each config option as a member
   * variable on this instance and triggers any defined setters. This
   * also sets initial state and triggers any listeners.
   *
   * @returns - This controller instance
   */
  protected initialize(): this {
    this.internalState = this.defaultState;
    this.internalConfig = this.defaultConfig;
    this.configure(this.initialConfig);
    this.update(this.initialState);
    return this;
  }
}

export default BaseController;
import BaseController from "../BaseController";
import { BaseBlockTrackerConfig, BaseBlockTrackerState } from "./IBlockTrackerController";

const sec = 1000;

const calculateSum = (accumulator: number, currentValue: number) => accumulator + currentValue;
const blockTrackerEvents: string[] = ["sync", "latest"];

export class BaseBlockTracker<
  T extends { idempotencyKey: string },
  C extends BaseBlockTrackerConfig,
  S extends BaseBlockTrackerState<T>
> extends BaseController<C, S> {
  name = "BaseBlockTracker";

  private _blockResetTimeout?: ReturnType<typeof setTimeout>;

  constructor({ config = {}, state = {} }: { config: Partial<C>; state: Partial<S> }) {
    super({ config, state });

    // config

    this.defaultState = {
      _currentBlock: { idempotencyKey: "" },
      _isRunning: false,
    } as S;

    this.defaultConfig = {
      blockResetDuration: 20 * sec,
    } as C;

    this.initialize();

    // bind functions for internal use
    this._onNewListener = this._onNewListener.bind(this);
    this._onRemoveListener = this._onRemoveListener.bind(this);
    this._resetCurrentBlock = this._resetCurrentBlock.bind(this);

    // listen for handler changes
    this._setupInternalEvents();
  }

  isRunning(): boolean {
    return this.state._isRunning;
  }

  getCurrentBlock(): T {
    return this.state._currentBlock;
  }

  async getLatestBlock(): Promise<T> {
    // return if available
    if (this.state._currentBlock) {
      return this.state._currentBlock;
    }
    // wait for a new latest block
    const latestBlock = await new Promise((resolve: (state: T) => void) => {
      this.once("latest", (newState: BaseBlockTrackerState<T>) => {
        if (newState._currentBlock) {
          resolve(newState._currentBlock);
        }
      });
    });
    // return newly set current block
    return latestBlock;
  }

  // dont allow module consumer to remove our internal event listeners
  removeAllListeners(eventName?: string): this {
    if (eventName) {
      super.removeAllListeners(eventName);
    } else {
      super.removeAllListeners();
    }
    // re-add internal events
    this._setupInternalEvents();
    // trigger stop check just in case
    this._onRemoveListener();
    return this;
  }

  /**
   * To be implemented in subclass.
   */
  protected _start(): void {
    // default behavior is noop
  }

  /**
   * To be implemented in subclass.
   */
  protected _end(): void {
    // default behavior is noop
  }

  protected _newPotentialLatest(newBlock: T): void {
    const currentBlock = this.state._currentBlock;
    // only update if blok number is higher
    if (currentBlock && newBlock.idempotencyKey === currentBlock.idempotencyKey) {
      return;
    }
    this._setCurrentBlock(newBlock);
  }

  private _setupInternalEvents(): void {
    // first remove listeners for idempotency
    this.removeListener("newListener", this._onNewListener);
    this.removeListener("removeListener", this._onRemoveListener);
    // then add them
    this.on("removeListener", this._onRemoveListener);
    this.on("newListener", this._onNewListener);
  }

  private _onNewListener(): void {
    this._maybeStart();
  }

  private _onRemoveListener(): void {
    // `removeListener` is called *after* the listener is removed
    if (this._getBlockTrackerEventCount() > 0) {
      return;
    }
    this._maybeEnd();
  }

  private _maybeStart(): void {
    if (this.state._isRunning) {
      return;
    }
    this.state._isRunning = true;
    // cancel setting latest block to stale
    this._cancelBlockResetTimeout();
    this._start();
  }

  private _maybeEnd(): void {
    if (!this.state._isRunning) {
      return;
    }
    this.state._isRunning = false;
    this._setupBlockResetTimeout();
    this._end();
  }

  private _getBlockTrackerEventCount(): number {
    return blockTrackerEvents.map((eventName) => this.listenerCount(eventName)).reduce(calculateSum);
  }

  private _setCurrentBlock(newBlock: T): void {
    const oldBlock = this.state._currentBlock;
    this.update({
      _currentBlock: newBlock,
    } as S);
    this.emit("latest", newBlock);
    this.emit("sync", { oldBlock, newBlock });
  }

  private _setupBlockResetTimeout(): void {
    // clear any existing timeout
    this._cancelBlockResetTimeout();
    // clear latest block when stale
    this._blockResetTimeout = setTimeout(this._resetCurrentBlock, this.config.blockResetDuration);

    // nodejs - dont hold process open
    if (this._blockResetTimeout.unref) {
      this._blockResetTimeout.unref();
    }
  }

  private _cancelBlockResetTimeout(): void {
    if (this._blockResetTimeout) {
      clearTimeout(this._blockResetTimeout);
    }
  }

  private _resetCurrentBlock(): void {
    this.update({ _currentBlock: { idempotencyKey: "" } } as Partial<S>);
  }
}
import { SafeEventEmitter } from "@toruslabs/openlogin-jrpc";
const filterNoop = () => true;
const internalEvents = ["newListener", "removeListener"];
const externalEventFilter = (name: string) => !internalEvents.includes(name);

interface EventEmitterProxyOpts {
  eventFilter?: ((name: string) => boolean) | string;
}

function getRawListeners<T extends SafeEventEmitter>(eventEmitter: T, name: string) {
  // prefer native
  return eventEmitter.rawListeners(name);
}

export default function createEventEmitterProxy<T extends SafeEventEmitter>(initialTarget: T, opts?: EventEmitterProxyOpts): T {
  // parse options
  const finalOpts = opts || {};
  let eventFilter = finalOpts.eventFilter || filterNoop;
  if (typeof eventFilter === "string" && eventFilter === "skipInternal") eventFilter = externalEventFilter;
  if (typeof eventFilter !== "function") throw new Error("createEventEmitterProxy - Invalid eventFilter");

  let target = initialTarget;

  let setTarget = (newTarget: T) => {
    const oldTarget = target;
    target = newTarget;

    oldTarget
      .eventNames()
      .filter(eventFilter as (name: string) => boolean)
      .forEach((name: string) => {
        getRawListeners(oldTarget, name).forEach((handler: (...args: any[]) => void) => newTarget.on(name, handler));
      });

    // remove old listeners
    oldTarget.removeAllListeners();
  };

  const proxy = new Proxy<T>({} as T, {
    get: (_, name) => {
      // override `setTarget` access
      if (name === "setTarget") return setTarget;
      return target[name];
    },
    set: (_, name, value) => {
      // allow `setTarget` overrides
      if (name === "setTarget") {
        setTarget = value;
        return true;
      }
      target[name] = value;
      return true;
    },
  });

  return proxy;
}
export default function createSwappableProxy<T extends object>(initialTarget: T): T {
  let target = initialTarget;

  let setTarget = (newTarget: T) => {
    target = newTarget;
  };

  const proxy = new Proxy<T>({} as T, {
    get: (_, name) => {
      // override `setTarget` access
      if (name === "setTarget") return setTarget;
      return target[name];
    },
    set: (_, name, value) => {
      // allow `setTarget` overrides
      if (name === "setTarget") {
        setTarget = value;
        return true;
      }
      target[name] = value;
      return true;
    },
  });

  return proxy;
}
import BaseController from "../BaseController";
import { BaseCurrencyControllerConfig, BaseCurrencyControllerState } from "./ICurrencyController";

// every ten minutes
const POLLING_INTERVAL = 600_000;

export class BaseCurrencyController<C extends BaseCurrencyControllerConfig, S extends BaseCurrencyControllerState> extends BaseController<C, S> {
  constructor({ config = {}, state }: { config: Partial<C>; state?: Partial<S> }) {
    super({ config, state });
    this.defaultState = {
      currentCurrency: "usd",
      conversionRate: 0,
      conversionDate: "N/A",
      nativeCurrency: "ETH",
    } as S;

    this.defaultConfig = {
      pollInterval: POLLING_INTERVAL,
    } as C;
    this.initialize();
  }

  //
  // PUBLIC METHODS
  //

  public getNativeCurrency(): string {
    return this.state.nativeCurrency;
  }

  public setNativeCurrency(nativeCurrency: string): void {
    this.update({
      nativeCurrency,
      ticker: nativeCurrency,
    } as S);
  }

  public getCurrentCurrency(): string {
    return this.state.currentCurrency;
  }

  public setCurrentCurrency(currentCurrency: string): void {
    this.update({ currentCurrency } as S);
  }

  /**
   * A getter for the conversionRate property
   *
   * @returns The conversion rate from ETH to the selected currency.
   *
   */
  public getConversionRate(): number {
    return this.state.conversionRate;
  }

  public setConversionRate(conversionRate: number): void {
    this.update({ conversionRate } as S);
  }

  /**
   * A getter for the conversionDate property
   *
   * @returns The date at which the conversion rate was set. Expressed in milliseconds since midnight of
   * January 1, 1970
   *
   */
  public getConversionDate(): string {
    return this.state.conversionDate;
  }

  public setConversionDate(conversionDate: string): void {
    this.update({ conversionDate } as S);
  }
}
import { JRPCEngine, JRPCMiddleware, JRPCRequest, JRPCResponse, SafeEventEmitter } from "@toruslabs/openlogin-jrpc";
import { ethErrors, serializeError } from "eth-rpc-errors";
import getCreateRandomId from "json-rpc-random-id";

import { BaseConfig, BaseState, IController } from "../interfaces";
export const createRandomId = getCreateRandomId();

export interface ProviderConfig {
  /**
   * Block explorer url for the chain
   * @example https://ropsten.etherscan.io
   */
  blockExplorerUrl: string;
  /**
   * Logo url for the base token
   */
  logo: string;
  /**
   * Name for ticker
   * @example 'Binance Token', 'Ethereum', 'Matic Network Token'
   */
  tickerName: string;
  /**
   * Symbol for ticker
   * @example BNB, ETH
   */
  ticker: string;
  /**
   * RPC target Url for the chain
   * @example https://ropsten.infura.io/v3/YOUR_API_KEY
   */
  rpcTarget: string;
  /**
   * Chain Id parameter(hex with 0x prefix) for the network. Mandatory for all networks. (assign one with a map to network identifier for platforms)
   * @example 0x1 for mainnet, 'loading' if not connected to anything yet or connection fails
   * @defaultValue 'loading'
   */
  chainId: string;
  /**
   * Display name for the network
   */
  displayName: string;
}

/**
 * Custom network properties
 * @example isEIP1559Compatible: true etc.
 */
export interface NetworkProperties {
  [key: string]: number | string | boolean;
}

/**
 *
 */
export interface NetworkState extends BaseState {
  /**
   * Chain Id for the current network
   */
  chainId: string;
  providerConfig: ProviderConfig;
  properties: NetworkProperties;
}

export interface NetworkConfig extends BaseConfig {
  providerConfig: ProviderConfig;
}

export interface INetworkController<C, S> extends IController<C, S> {
  /**
   * Gets the chainId of the network
   */
  getNetworkIdentifier(): string;

  /**
   * Sets provider for the current network controller
   * @param providerConfig - Provider config object
   */
  setProviderConfig(providerConfig: ProviderConfig): void;
  /**
   * Connects to the rpcUrl for the current selected provider
   */
  lookupNetwork(): Promise<void>;
}

export type BlockData = string | string[];

export type Block = Record<string, BlockData>;

export type SendAsyncCallBack = (err: Error, providerRes: JRPCResponse<Block>) => void;

export type SendCallBack<U> = (err: any, providerRes: U) => void;

export type Payload = Partial<JRPCRequest<string[]>>;

export interface RequestArguments<T> {
  method: string;
  params?: T;
}
export type Maybe<T> = T | Partial<T> | null | undefined;

export interface SafeEventEmitterProvider extends SafeEventEmitter {
  sendAsync: <T, U>(req: JRPCRequest<T>) => Promise<U>;
  send: <T, U>(req: JRPCRequest<T>, callback: SendCallBack<U>) => void;
  request: <T, U>(args: RequestArguments<T>) => Promise<Maybe<U>>;
}

export interface ExtendedJsonRpcRequest<T> extends JRPCRequest<T> {
  skipCache?: boolean;
}

export function providerFromEngine(engine: JRPCEngine): SafeEventEmitterProvider {
  const provider: SafeEventEmitterProvider = new SafeEventEmitter() as SafeEventEmitterProvider;
  // handle both rpc send methods
  provider.sendAsync = async <T, U>(req: JRPCRequest<T>) => {
    const res = await engine.handle(req);
    if (res.error) {
      const err = serializeError(res.error, {
        fallbackError: {
          message: res.error?.message || res.error.toString(),
          code: res.error?.code || -32603,
        },
      });

      throw ethErrors.rpc.internal(err);
    }
    return res.result as U;
  };
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  provider.send = <T, U>(req: JRPCRequest<T>, callback: (error: any, providerRes: U) => void) => {
    if (typeof callback !== "function") {
      throw new Error('Must provide callback to "send" method.');
    }
    engine.handle(req, callback);
  };
  // forward notifications
  if (engine.on) {
    engine.on("notification", (message: string) => {
      provider.emit("data", null, message);
    });
  }

  provider.request = async <T, U>(args: RequestArguments<T>) => {
    const req: JRPCRequest<T> = {
      ...args,
      id: createRandomId(),
      jsonrpc: "2.0",
    };
    const res = await provider.sendAsync(req);
    return res as U;
  };
  return provider;
}

export function providerFromMiddleware(middleware: JRPCMiddleware<string[], unknown>): SafeEventEmitterProvider {
  const engine = new JRPCEngine();
  engine.push(middleware);
  const provider: SafeEventEmitterProvider = providerFromEngine(engine);
  return provider;
}

export function providerAsMiddleware(provider: SafeEventEmitterProvider): JRPCMiddleware<unknown, unknown> {
  return async (req, res, _next, end) => {
    // send request to provider
    try {
      const providerRes: unknown = await provider.sendAsync<unknown, unknown>(req);
      res.result = providerRes;
      return end();
    } catch (error) {
      return end(error.message);
    }
  };
}
